import './App.css';
import {Logo,Constants} from './Utils/ImportFile';
import React from 'react';
import HomeSection from './Components/HomeSection/HomeSection';
import { motion, useScroll ,useAnimationFrame} from "framer-motion";

function App() {
  const { scrollYProgress } = useScroll();
  const ref = React.useRef(null);

  useAnimationFrame((t) => {
    const rotate = Math.sin(t / 10000) * 200;
    const y = (1 + Math.sin(t / 1000)) * -50;
    ref.current.style.transform = `translateY(${y}px) rotateX(${rotate}deg) rotateY(${rotate}deg)`;
  });


  React.useEffect(()=>{
    console.log(Constants.NavLinks)
    setTimeout(()=>{
      document.getElementsByClassName('logoContainer')[0].setAttribute('style','display:none')
    },2500)
  })

  return (
     <>
          <motion.div
        className="progress-bar"
        style={{ scaleX: scrollYProgress }}
      />
     <Logo />

     <HomeSection />
     <div className="container">
      <div className="cube" ref={ref}>
        <div className="side front" />
        <div className="side left" />
        <div className="side right" />
        <div className="side top" />
        <div className="side bottom" />
        <div className="side back" />
      </div>
    </div>
     </>
  );
}

export default App;
